import './App.css';
import Axios from 'axios';
import { useState, useEffect } from 'react';

function App() {

  const [excuseText, setExcuseText] = useState(null);

  const fetchExcuse = (excuse) => {
    Axios.get(`https://excuser-three.vercel.app/v1/excuse/${excuse}`)
    .then((res) => {
      setExcuseText(res.data);
    });
  }

  useEffect(() => {
    fetchExcuse("");
  }, []);

  return (
    <div className='App'>
      <p>Choose excuse</p>
      <button onClick={() => fetchExcuse("Work")}>Work</button>

      <p>{excuseText.excuse}</p>
    </div>
  );
};


export default App;
