      }
      return task;
    })); 
  }


  return (
    <div className='App'>
      <input type='text' onChange={handleChange}/>
      <button onClick={addTodoList}>Add Task</button>

      {todoList.map((task) => {
        return (
          <div>
            <h1> {task.name} {task.completed}</h1>
            <button onClick={() => deleteTask(task.id)}>x</button>
            <button onClick={() => changeCompletion(task.id)}>	&#x2714;</button>
          </div>
        );
      })}

    </div>
  );
};


export default App;
