import './App.css';
import axios, { Axios } from 'axios';
import { useState, useEffect } from 'react';

function App() {

  const [excuse, setExcuse] = useState("");
  const [excuseText, setExcuseText] = useState("");

  const fetchExcuse = () => {
    Axios.get(`https://excuser-three.vercel.app/v1/excuse/${excuse}`)
    .then((res) => {
      setExcuseText(res.data.excuse);
    })
  }

  useEffect(() => {
    fetchExcuse();
  }, []);

  return (
    <div className='App'>
      <p>Choose excuse</p>
      <button onClick={() => {setExcuse("Work"); fetchExcuse}}>Work</button>
    </div>
  );
};


export default App;
